package string;import java.io.*;import java.util.Scanner;public class Test {    public static void main(String[] args) {        String path = "src/resource/sample.txt";        File file = new File(path);        try {            BufferedWriter br = new BufferedWriter(new FileWriter(file, true));            Scanner sc = new Scanner(System.in);            String text = sc.nextLine();            String newLine = "\n";            br.write(newLine);            br.write(text);            br.close();        } catch (IOException e) {            e.printStackTrace();        }    }}